import React, { useState, useLayoutEffect } from 'react';
import Button from "./Button";
import './css/fonts.css';
import './css/index.css';
import logic from "./logic";

function RecipeRipper() {
 const [isPressed, setIsPressed] = useState(false);
 const [buttons, setButtons] = useState([
  { id: 1, name: 'start' },
  { id: 2, name: 'next' },
  { id: 3, name: 'send' },
  ]);

 function useWindowSize() {
  const [size, setSize] = useState([0, 0]);
  useLayoutEffect(() => {
    function updateSize() {
      setSize([window.innerWidth, window.innerHeight]);
    }
    window.addEventListener('resize', updateSize);
    updateSize();
    return () => window.removeEventListener('resize', updateSize);
  }, []);
  return size;
}


return (
  <div>
  <h1 id="title">RECIPE RIPPER</h1>
  <div id="page" style={{paddingTop: '30px' }}>
  <div>
  <div id="textareas">
  <label htmlFor="textarea1">RECIPE RIPPER</label>
  <textarea id="textarea1" cols={32}></textarea>
  <textarea id="textarea2" cols={32} style={{ display: 'none' }}></textarea>
  </div>

  <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
  {buttons.map((button) => (
    <Button
    key={button.id}
    name={button.name}
    text={button.name}
    isPressed={isPressed}
    setIsPressed={setIsPressed}
    id={button.id}
    />
    ))}
  </div>           
  </div>
  </div>
  </div>
  );
}

export default RecipeRipper;
