import React, { useState } from 'react';
import Button from "./Button";
import './css/fonts.css';
import './css/index.css';
import logic from "./logic";

function RecipeRipper() {
   const [isPressed, setIsPressed] = useState(false);

   const buttons = [
    { id: 1, name: 'start' },
    { id: 2, name: 'next' },
    { id: 3, name: 'send' },
  ];


  return (
    <div>
      <head>
        <meta name="viewport" content="width=device-width, initial-scale=1" />
      </head>
      <h1 id="title">RECIPE RIPPER</h1>
      <div style={{ display: 'flex', justifyContent: 'center' }} id="page">
        <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
        <div id="textareas">
            <label htmlFor="textarea1">RECIPE RIPPER</label>
            <textarea id="textarea1" cols={32}></textarea>
            <textarea id="textarea2" cols={32} style={{ display: 'none' }}></textarea>
        </div>

        <div style={{ display: 'flex', justifyContent: 'center', paddingTop: '20px' }}>
           {buttons.map((button) => (
        <Button
          key={button.id}
          name={button.name}
          text={button.name}
          isPressed={isPressed}
          setIsPressed={setIsPressed}
          id={button.id}
        />
      ))}
        </div>           
        </div>
      </div>
    </div>
  );
}

export default RecipeRipper;
