function handleLogic(id) {
  if(id == '1') {
        document.getElementById('title').style.display = 'none';
        document.getElementById('button1').style.display = 'none';
        document.getElementById('button2').style.display = 'inline';
        document.getElementById('textareas').style.display = 'inline';
        document.getElementById('textarea1').style.display = 'inline';
        document.getElementById('textarea1').value = '1 egg';
        document.getElementById('textarea2').value = 'Fry egg on stove';
  }
  if(id == '2'){
    document.getElementById('textarea1').style.display = 'none';
    document.getElementById('textarea2').style.display = 'inline';
    document.getElementById('button2').style.display = 'none';
    document.getElementById('button3').style.display = 'inline';
    var ingredients = separateIngredients(document.getElementById('textarea1').value);
    console.log(ingredients);
    sessionStorage.setItem("ingredients", ingredients);   
  }
  if(id == '3'){
    document.getElementById('textarea2').style.display = 'inline';
    document.getElementById('button3').style.display = 'none';

    var instructions = document.getElementById('textarea2').value;
    substituteIngredients(ingredients, instructions);
  }
}
function disappear(props){
  alert(props.name);
  props.name = 'next';
}
export  {handleLogic, disappear};

function separateIngredients(ingredients) {
  const ingredientsList = ingredients.split("\n");
  const separatedIngredients = [];
  for (let i = 0; i < ingredientsList.length; i++) {
    const ingredient = ingredientsList[i].trim();
    if (ingredient !== "") {
      const amount = ingredient.match(/^[\d./\u00BC\u00BD]+/)[0];
      const name = ingredient.replace(amount, "").trim();
      separatedIngredients.push({ name, amount });
    }
  }
  return separatedIngredients;
}

function substituteIngredients(ingredients, instructions) {
  let result = instructions;
  for (let i = 0; i < ingredients.length; i++) {
    let ingredient = ingredients[i];
    let regex = new RegExp(ingredient.name, 'g');
    result = result.replace(regex, ingredient.amount);
  }
  return result;
}
